import React from 'react';
import './Slider.css';

const Slider = ({ value, onChange, min = 1, max = 7, labels }) => {
  // 如果没有传入 labels，生成默认的数字标签
  const displayLabels = labels || Array.from({ length: max - min + 1 }, (_, i) => min + i);

  const handleChange = (e) => {
    onChange(Number(e.target.value));
  };

  return (
    <div className="slider-container">
      <input
        type="range"
        min={min}
        max={max}
        value={value || Math.ceil((max + min) / 2)} // 默认中间值，或者传入的 value
        onChange={handleChange}
        className="slider-input"
      />
      <div className="slider-labels">
        {displayLabels.map((label, index) => {
          const val = min + index;
          const isActive = value === val;
          return (
            <div 
              key={val} 
              className={`slider-label-item ${isActive ? 'active' : ''}`}
              onClick={() => onChange(val)}
            >
              {label}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Slider;
