import React, { useState, useMemo } from 'react';
import { questions } from '../data/questions';
import Slider from './Slider';
import { calculateDimensionScore, getAttachmentStyle } from '../utils/scoring';

const LABELS_7_POINT = [
  "强烈反对", "反对", "有点反对", "中立/混合", "有点同意", "同意", "强烈同意"
];

const LABELS_5_POINT = [
  "几乎从不", "有时", "约一半时间", "大多数时间", "几乎总是"
];

const LABELS_5_POINT_AGREE = [
  "强烈反对", "反对", "中立", "同意", "强烈同意"
];

const Quiz = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [answers, setAnswers] = useState({});
  const [isFinished, setIsFinished] = useState(false);

  // 扁平化所有题目，构建一个线性的答题流
  const quizItems = useMemo(() => {
    const list = [];

    // 1. Profile
    questions.profile.forEach(q => {
      list.push({ ...q, category: 'profile', sectionTitle: '个人资料' });
    });

    // 2. Attachment (Mother, Father, Partner, General)
    questions.attachment.sections.forEach(section => {
      questions.attachment.items.forEach((item, idx) => {
        list.push({
          ...item,
          category: 'attachment',
          sectionId: section.id,
          sectionTitle: idx === 0 ? section.title_zh : null, // 只在每组第一题显示标题
          sectionDesc: idx === 0 ? section.description_zh : null,
          uniqueId: `${section.id}_${item.id}`, // 唯一ID用于存储答案
          min: 1,
          max: 7,
          labels: LABELS_7_POINT
        });
      });
    });

    // 3. Self Esteem
    questions.selfEsteem.forEach((q, idx) => {
      list.push({
        ...q,
        id: `se_${idx}`,
        uniqueId: `se_${idx}`,
        category: 'selfEsteem',
        sectionTitle: idx === 0 ? '自尊量表' : null,
        min: 1,
        max: 5,
        labels: LABELS_5_POINT_AGREE
      });
    });

    // 4. DERS
    questions.ders.forEach((q, idx) => {
      list.push({
        ...q,
        id: `ders_${idx}`,
        uniqueId: `ders_${idx}`,
        category: 'ders',
        sectionTitle: idx === 0 ? '情绪调节量表' : null,
        min: 1,
        max: 5,
        labels: LABELS_5_POINT
      });
    });

    return list;
  }, []);

  const currentQuestion = quizItems[currentStep];
  const totalSteps = quizItems.length;
  const progress = ((currentStep + 1) / totalSteps) * 100;

  const handleAnswer = (value) => {
    setAnswers(prev => ({
      ...prev,
      [currentQuestion.uniqueId || currentQuestion.id]: value
    }));
  };

  const handleNext = () => {
    if (currentStep < totalSteps - 1) {
      setCurrentStep(prev => prev + 1);
      window.scrollTo(0, 0);
    } else {
      finishQuiz();
    }
  };

  const handlePrev = () => {
    if (currentStep > 0) {
      setCurrentStep(prev => prev - 1);
    }
  };

  const finishQuiz = () => {
    setIsFinished(true);
    // 这里可以进行最终的计算
    console.log("Answers:", answers);
  };

  // 渲染不同类型的题目输入
  const renderInput = () => {
    const val = answers[currentQuestion.uniqueId || currentQuestion.id];

    if (currentQuestion.type === 'choice') {
      return (
        <div className="options-container">
          {currentQuestion.options.map(opt => (
            <button
              key={opt.value}
              className={`option-btn ${val === opt.value ? 'selected' : ''}`}
              onClick={() => handleAnswer(opt.value)}
            >
              {opt.label_zh}
            </button>
          ))}
        </div>
      );
    }

    if (currentQuestion.type === 'text') {
      return (
        <input 
          type="text" 
          className="text-input"
          value={val || ''} 
          onChange={(e) => handleAnswer(e.target.value)}
          placeholder="请输入..."
        />
      );
    }

    // 默认为 Slider
    return (
      <Slider
        value={val}
        onChange={handleAnswer}
        min={currentQuestion.min}
        max={currentQuestion.max}
        labels={currentQuestion.labels}
      />
    );
  };

  if (isFinished) {
    return (
      <div className="card">
        <h2>测试完成！</h2>
        <p>正在生成您的依恋类型报告...</p>
        {/* 结果页组件将放在这里 */}
      </div>
    );
  }

  const isAnswered = answers[currentQuestion.uniqueId || currentQuestion.id] !== undefined;

  return (
    <div className="quiz-container">
      {/* 进度条 */}
      <div className="progress-bar-container">
        <div className="progress-bar" style={{ width: `${progress}%` }}></div>
      </div>

      {/* 章节标题页 (如果当前题目有 sectionTitle) */}
      {currentQuestion.sectionTitle && (
        <div className="section-header">
          <h2>{currentQuestion.sectionTitle}</h2>
          {currentQuestion.sectionDesc && <p>{currentQuestion.sectionDesc}</p>}
          <hr />
        </div>
      )}

      <div className="card question-card">
        <div className="question-meta">
          Question {currentStep + 1} of {totalSteps}
        </div>

        <h3 className="question-text">
          {currentQuestion.text_zh}
        </h3>
        <p className="question-subtext">{currentQuestion.text_en}</p>

        <div className="input-area">
          {renderInput()}
        </div>

        <div className="navigation-buttons">
          <button 
            className="btn secondary" 
            onClick={handlePrev} 
            disabled={currentStep === 0}
          >
            上一题
          </button>
          <button 
            className="btn primary" 
            onClick={handleNext}
            disabled={!isAnswered}
          >
            {currentStep === totalSteps - 1 ? '提交' : '下一题'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Quiz;
