import React from "react";
import "./Slider.css";

const Slider = ({ value, onChange, min = 1, max = 7, labels }) => {
  // 如果没有传入 labels，生成默认的数字标签
  const displayLabels =
    labels || Array.from({ length: max - min + 1 }, (_, i) => min + i);

  const handleChange = (e) => {
    onChange(Number(e.target.value));
  };

  // 计算背景进度条样式 (紫色填充)
  const percentage = ((value - min) / (max - min)) * 100;
  const backgroundStyle = {
    background: `linear-gradient(to right, #5e548e 0%, #5e548e ${percentage}%, #e0e0e0 ${percentage}%, #e0e0e0 100%)`,
  };

  // 获取当前选中的标签文本
  const currentLabelText = displayLabels[value - min];

  return (
    <div className="slider-container">
      {/* 当前值显示 */}
      <div className={`slider-current-value ${value ? 'visible' : ''}`}>
        {currentLabelText}
      </div>

      <input
        type="range"
        min={min}
        max={max}
        value={value || Math.ceil((max + min) / 2)} 
        onChange={handleChange}
        className="slider-input"
        style={backgroundStyle}
      />

      <div className="slider-labels">
        {displayLabels.map((label, index) => {
          const val = min + index;
          const isActive = value === val;
          const leftPos = (index / (displayLabels.length - 1)) * 100;

          return (
            <React.Fragment key={val}>
              {/* 轨道上的小圆点 */}
              <div 
                className="slider-dot"
                style={{ left: `${leftPos}%`, transform: 'translateX(-50%)' }}
              />

              {/* 文字标签 */}
              <div 
                className={`slider-label-item ${isActive ? 'active' : ''}`}
                onClick={() => onChange(val)}
                style={{ left: `${leftPos}%` }} 
              >
                {label}
              </div>
            </React.Fragment>
          );
        })}
      </div>
    </div>
  );
};

export default Slider;
