import React, { useMemo } from 'react';
import { 
  ScatterChart, Scatter, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, ReferenceLine, Cell, Label
} from 'recharts';
import { questions } from '../data/questions';
import { calculateDimensionScore, getAttachmentStyle } from '../utils/scoring';
import './ResultPage.css';

const ResultPage = ({ answers, onRestart }) => {

  // 计算所有部分的得分
  const results = useMemo(() => {
    const sections = questions.attachment.sections;
    const items = questions.attachment.items;

    return sections.map(section => {
      // 构建该部分的答案子集
      // 实际上 calculateDimensionScore 需要的是 { questionId: score }
      // 我们的 answers 是 { sectionId_itemId: score }
      // 所以我们需要把 answers 映射回 { itemId: score } 或者修改 calculateDimensionScore
      // 这里我们选择构建一个临时的 answer 对象传给 calculateDimensionScore

      const sectionAnswers = {};
      items.forEach(item => {
        const uniqueId = `${section.id}_${item.id}`;
        if (answers[uniqueId] !== undefined) {
          sectionAnswers[item.id] = answers[uniqueId];
        }
      });

      const scores = calculateDimensionScore(sectionAnswers, items);
      const style = getAttachmentStyle(scores.anxiety, scores.avoidance);

      return {
        id: section.id,
        name: section.title_zh.replace('关系结构：', ''), // 简化名称
        anxiety: parseFloat(scores.anxiety),
        avoidance: parseFloat(scores.avoidance),
        style: style
      };
    });
  }, [answers]);

  // 找到一般依恋风格 (General) 用于主要展示
  const generalResult = results.find(r => r.id === 'general') || results[0];

  // 图表数据
  const chartData = results.map(r => ({
    x: r.avoidance,
    y: r.anxiety,
    name: r.name,
    attachmentType: r.style // 重命名 style 为 attachmentType，避免与 React style 属性冲突
  }));

  // 自定义 Tooltip
  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      return (
        <div className="custom-tooltip">
          <p className="label"><strong>{data.name}</strong></p>
          <p>焦虑分数: {data.y}</p>
          <p>回避分数: {data.x}</p>
          <p>类型: {data.attachmentType}</p>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="result-container">
      <div className="result-header">
        <h2>您的依恋类型测试结果</h2>
        <p>基于 ECR-RS 关系结构问卷分析</p>
      </div>

      <div className="chart-container">
        {/* 背景象限标签 */}
        <div className="quadrant-label q-preoccupied">焦虑型 (Preoccupied)</div>
        <div className="quadrant-label q-fearful">恐惧/混乱型 (Fearful)</div>
        <div className="quadrant-label q-secure">安全型 (Secure)</div>
        <div className="quadrant-label q-dismissive">回避型 (Dismissive)</div>

        <ResponsiveContainer width="100%" height="100%">
          <ScatterChart
            margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
          >
            <CartesianGrid />
            <XAxis 
              type="number" 
              dataKey="x" 
              name="回避" 
              domain={[1, 7]} 
              label={{ value: '回避维度 (Avoidance)', position: 'bottom', offset: 0 }} 
            />
            <YAxis 
              type="number" 
              dataKey="y" 
              name="焦虑" 
              domain={[1, 7]} 
              label={{ value: '焦虑维度 (Anxiety)', angle: -90, position: 'left' }} 
            />
            <Tooltip content={<CustomTooltip />} />

            {/* 中心分割线 (假设阈值为 4) */}
            <ReferenceLine x={4} stroke="#ccc" strokeDasharray="3 3" />
            <ReferenceLine y={4} stroke="#ccc" strokeDasharray="3 3" />

            <Scatter name="Results" data={chartData} fill="#5e548e">
              {chartData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={entry.id === 'general' ? '#ff6b6b' : '#5e548e'} />
              ))}
            </Scatter>
          </ScatterChart>
        </ResponsiveContainer>
      </div>

      <div className="result-details">
        <div className="result-card">
          <h3>一般依恋风格 (General)</h3>
          <p>这是您在一般亲密关系中的倾向。</p>
          <div className="score-bar">
            <span className="score-label">焦虑</span>
            <div className="score-track">
              <div className="score-fill" style={{ width: `${(generalResult.anxiety / 7) * 100}%` }}></div>
            </div>
            <span className="score-value">{generalResult.anxiety}</span>
          </div>
          <div className="score-bar">
            <span className="score-label">回避</span>
            <div className="score-track">
              <div className="score-fill" style={{ width: `${(generalResult.avoidance / 7) * 100}%` }}></div>
            </div>
            <span className="score-value">{generalResult.avoidance}</span>
          </div>
          <p><strong>判定结果：</strong> {generalResult.style}</p>
        </div>

        {/* 其他关系的简要列表 */}
        <div className="other-results">
          <h4>其他关系维度：</h4>
          <ul>
            {results.filter(r => r.id !== 'general').map(r => (
              <li key={r.id} style={{ marginBottom: '10px', color: '#555' }}>
                <strong>{r.name}:</strong> {r.style} (焦虑: {r.anxiety}, 回避: {r.avoidance})
              </li>
            ))}
          </ul>
        </div>
      </div>

      <button className="restart-btn" onClick={onRestart}>
        重新测试
      </button>
    </div>
  );
};

export default ResultPage;
