import React, { useMemo } from "react";
import {
  ScatterChart,
  Scatter,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  ReferenceLine,
  Cell,
  ReferenceArea,
  Label,
} from "recharts";
import { questions } from "../data/questions";
import { calculateDimensionScore, getAttachmentStyle } from "../utils/scoring";
import "./ResultPage.css";

// 定义象限背景色
const QUADRANT_COLORS = {
  secure: "#d0f0c0", // 左下：安全型 (绿色系)
  anxious: "#ffe0b2", // 右下：焦虑型 (橙色系)
  avoidant: "#bbdefb", // 左上：回避型 (蓝色系)
  disorganized: "#f8bbd0", // 右上：恐惧型 (粉色系)
};

// 定义不同关系的点的颜色
const POINT_COLORS = {
  mother: "#d32f2f", // 红色
  father: "#388e3c", // 绿色
  partner: "#1976d2", // 蓝色
  general: "#212121", // 黑色
};

const ATTACHMENT_DESCRIPTIONS = {
