              x1={4}
              x2={7}
              y1={4}
              y2={7}
              fill={QUADRANT_COLORS.disorganized}
              fillOpacity={0.4}
            >
              <Label
                value="恐惧/混乱型 (Disorganized)"
                position="center"
                fill="#c62828"
                fontSize={14}
                fontWeight="bold"
              />
            </ReferenceArea>

            {/* 左下: Secure (低焦虑, 低回避) -> x: 1-4, y: 1-4 */}
            <ReferenceArea
              x1={1}
              x2={4}
              y1={1}
              y2={4}
              fill={QUADRANT_COLORS.secure}
              fillOpacity={0.4}
            >
              <Label
                value="安全型 (Secure)"
                position="center"
                fill="#2e7d32"
                fontSize={14}
                fontWeight="bold"
              />
            </ReferenceArea>

            {/* 右下: Anxious (高焦虑, 低回避) -> x: 4-7, y: 1-4 */}
            <ReferenceArea
              x1={4}
              x2={7}
              y1={1}
              y2={4}
              fill={QUADRANT_COLORS.anxious}
              fillOpacity={0.4}
            >
              <Label
                value="焦虑型 (Anxious)"
                position="center"
                fill="#ef6c00"
                fontSize={14}
                fontWeight="bold"
              />
            </ReferenceArea>

            {/* 中心分割线 */}
            <ReferenceLine x={4} stroke="#666" strokeWidth={1} />
            <ReferenceLine y={4} stroke="#666" strokeWidth={1} />

            {/* 数据点 */}
            <Scatter name="Results" data={chartData}>
              {chartData.map((entry, index) => (
                <Cell
                  key={`cell-${index}`}
                  fill={POINT_COLORS[entry.id] || "#000"}
                />
              ))}
            </Scatter>
          </ScatterChart>
        </ResponsiveContainer>
      </div>

      {/* 结果详情列表 (仿照截图样式) */}
      <div className="result-details-list">
        <h3>详细得分</h3>
        <div className="score-list">
          {results.map((r) => (
            <div key={r.id} className="score-item">
              <div className="score-item-header">
                <span
                  className="color-dot"
                  style={{ backgroundColor: POINT_COLORS[r.id] || "#000" }}
                ></span>
                <span className="score-name">{r.name}</span>
              </div>
              <div className="score-values">
                <span>
                  焦虑: <strong>{r.anxiety}</strong>
                </span>
                <span className="divider">|</span>
                <span>
                  回避: <strong>{r.avoidance}</strong>
                </span>
              </div>
              <div className="score-type">{r.attachmentType}</div>
            </div>
          ))}
        </div>
      </div>

      <button className="restart-btn" onClick={onRestart}>
        重新测试
      </button>
    </div>
  );
};

export default ResultPage;
